import React, { useState } from 'react';
import { Outlet, NavLink } from "react-router-dom";
import Nav from 'react-bootstrap/Nav';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import ReactRoundedImage from "react-rounded-image";
import { useSelector, useDispatch } from 'react-redux'
import {addLoginUser, logoutAction, selectCurrentUser} from './LoginPage/currentUserSlice'


//component unit
function LoginLink() {
  const stateCurrentUser = useSelector(selectCurrentUser);
  const username = stateCurrentUser.username;
  const handleLinkOnClick= (e) => {
    console.log(stateCurrentUser.username);
  };

  if(stateCurrentUser.username == "NOT_A_USER"){
    return (<NavLink href="/login" className='headers'>{"Login"}</NavLink>);
  }else{
    return (<NavLink onClick={handleLinkOnClick} href="/login" className='headers'>Logout</NavLink>);
  }

}

function Layout(){

  //get the current user's attributes
  const stateCurrentUser = useSelector(selectCurrentUser);
  const userid = stateCurrentUser.id;
  const username = stateCurrentUser.username;
  const avatar = stateCurrentUser.avatar;

  //the below section handle the re-routing of clicking the username button
  const [clickProfile, setClickProfile] = useState(false);

  const routeChange = () =>{
    setClickProfile(true);
  }

  return (
    <>

    {/* the pages Nav for development use */}
    <div>
    <Navbar>
        <Nav defaultActiveKey="/" as="ul">
          <Nav.Item as="li">
            <NavLink to="/" className='headers2'>Home</NavLink>
          </Nav.Item>
          <Nav.Item as="li">
            <NavLink to="/user" className='headers2'>UserPage</NavLink>
          </Nav.Item>
          <Nav.Item as="li">
            <NavLink to="/userNew" className='headers2'>OtherUser</NavLink>
          </Nav.Item>
          <Nav.Item as="li">
            <NavLink to="/register" className='headers2'>SignUpPage</NavLink>
          </Nav.Item>
          <Nav.Item as="li">
            <NavLink to="/upload" className='headers2'>UploadPostPage</NavLink>
          </Nav.Item>
          <Nav.Item as="li">
            <NavLink to="/login" className='headers2'>LoginPage</NavLink>
          </Nav.Item>
          <Nav.Item as="li">
            <NavLink to="/feed" className='headers2'>FeedPage</NavLink>
          </Nav.Item>
        </Nav>
      </Navbar>
    </div>

    {/* the logo "brand" bar */}
    <div className="top_half_page">
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
        <Container>
          <Col sm={6}>
          </Col>
          <Col>
                <Nav className="justify-content-end" activeKey="/">
                  <Nav.Item>
                    <ReactRoundedImage
                      image={avatar}
                      roundedColor="#ffffff"
                      imageWidth="35"
                      imageHeight="35"
                      roundedSize="2"
                      borderRadius="20"
                    />
                  </Nav.Item>
                  <Nav.Item>
                    <NavLink to='/user' className='headers'>Username</NavLink>
                  </Nav.Item>
                  <Nav.Item>
                    <NavLink to="/register" className='headers'>Sign up</NavLink>
                  </Nav.Item>
                  <Nav.Item>
                    {/* handle login to logout */}
                    <LoginLink />
                  </Nav.Item>
                  <Nav.Item>
                    <NavLink eventKey="link-2" className='headers'>About</NavLink>
                  </Nav.Item>
                  <Nav.Item>
                    <NavLink eventKey="disabled" className='headers' disabled>
                      Contact Us
                    </NavLink>
                  </Nav.Item>
                </Nav>
          </Col>
        </Container>
      </Navbar>

      <Row>
        <Col sm={8}>
          <Navbar.Brand style={{paddingLeft: "rem"}}>
            <img src={require("../images/pennsgram_logo.png")} alt="logo" width="527" height="149"></img>
          </Navbar.Brand>
        </Col>
        <Col >
          <InputGroup className="mb-1" style={{paddingTop: "2rem", paddingRight: "10rem"}}>
            <Form.Control
              placeholder="search username"
              aria-label="user's username"
              aria-describedby="basic-addon2"
            />
            <Button variant="secondary" id="button-addon2">
              search
            </Button>
          </InputGroup>
        </Col>
      </Row>
      <Row style={{paddingTop: "5rem", paddingLeft: "rem"}}></Row>
    </div>

      <Outlet />
    </>
  )
};

export default Layout;
