import "../Styles.css";
import React from "react";
import { Header } from "../components/Header";
import { Footer } from "../components/Footer";
import { Button } from "../components/Button";
import { SearchBar } from "../components/SearchBar";
import MyPhoto from "../images/user_pic1.png";
import ReactRoundedImage from "react-rounded-image";

function UserPage(props) {
    const follow = () => {
        if (this.props.follow == "Follow") {
            this.props.follow = "Following"
        } else {
            this.props.follow = "Follow";
        }
    }
  return (
    <div className="top_half_page">
      <div className="sticky-md-top">
        <img
          src={require("../images/pennsgram_logo.png")}
          alt="logo"
          width="234"
          height="66"
        ></img>

      </div>
      <div className="right">
        <Header page="user" />
        <SearchBar />
        <ReactRoundedImage
          image={MyPhoto}
          roundedColor="#321124"
          imageWidth="100"
          imageHeight="100"
          roundedSize="2"
          borderRadius="100"
        />
      </div>
      <div className="middle">
        <button className="btn" onClick={follow}> {props.follow} </button>
      </div>
    </div>
  );
}
export default UserPage;
