import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Card from 'react-bootstrap/Card';
import Stack from 'react-bootstrap/Stack';

//define a inside components
function Card_customed(props){
    //props is a post fed from a following user
    const imageSource = props.imageSource;
    return (
        <Card style={{ width: '18rem' }}>
          <Card.Title>
            {props.author}
            </Card.Title>
          <Card.Img variant="top" rounded="true" src={require(imageSource)} />
          <Card.Body>
            <Card.Text>
              {props.description}
            </Card.Text>
            <ButtonGroup aria-label="like,comment,message">
                <Button variant="light">ü•∞</Button>
                <Button variant="light">üí¨</Button>
                <Button variant="light">‚úâÔ∏è</Button>
            </ButtonGroup>
          </Card.Body>
        </Card>
        )
}
function FeedPage() {
    const feedPost1 = {author:"UserFollowing 1", 
    description:"Some description about the post.",
    imageSource: "./tiger.jpeg"};

    const feedPost2 = {author:"UserFollowing 2", 
    description:"Some description about the post."};

    const feedPost3 = {author:"UserFollowing 3", 
    description:"Some description about the post."};

  return (
    <>


    <Stack gap={3}>
      <div className="bg-light border">
        <Card_customed props={feedPost1} />
        </div>
      <div className="bg-light border">
        Second item
        </div>
      <div className="bg-light border">
        Third item
        </div>
    </Stack>
    </>
  );
}

export default FeedPage;
