import "../Styles.css";
import React, { useState } from "react";
import { Header } from "../components/Header";
import { Footer } from "../components/Footer";
import { Button } from "../components/Button";
import { SearchBar } from "../components/SearchBar";
import MyPhoto from "../images/user_pic1.png";
import ReactRoundedImage from "react-rounded-image";

function UserPage(props) {
  const [isFollowing, setIsFollowing] = useState(false);

  const addFollow = () => {
    if (isFollowing) {
      setIsFollowing(false);
    } else {
      setIsFollowing(true);
    }
  };

  return (
    <div className="top_half_page">
      <div className="sticky-md-top">
        <img
          src={require("../images/pennsgram_logo.png")}
          alt="logo"
          width="234"
          height="66"
        ></img>
      </div>
      <div className="right">
        <Header page="user" />
        <SearchBar />
        <ReactRoundedImage
          image={MyPhoto}
          roundedColor="#321124"
          imageWidth="100"
          imageHeight="100"
          roundedSize="2"
          borderRadius="100"
        />
      </div>
      <div className="for-other-user">
        <button className="btn" onClick={addFollow}>
          {" "}
          {isFollowing ? "Following" : "Follow"}
        </button>
        <div>User Name</div>
        <div>Some Description</div>
      </div>
    <div className="bot_half_page" color="white">

        <button className="new post">

        </button>

    </div>
    </div>
  );
}
export default UserPage;
